//---------------------------------
//removes element from array and updates indexes
//---------------------------------
function delArrayElem($array,$id)
{
  for ($i = $id ; $i < count($array)-1 ; $i++)
  {
      $array[$i]=$array[$i+1];  //TODO: may be optimized with "while"
  }
  unset ($array[count($array)-1]);
  return $array;
}


