//--------------------
//connect to DB
//--------------------
function dbConnect($server="localhost",$db="albom",$user="albom",$pass="albom")
{
  // Create connection
  $conn = new mysqli($server, $user, $pass,$db);

  // Check connection
  if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
  }
  return $conn; 
}// don't forget to close a connection with   $conn->close();



//---------------------
//create initial table
//---------------------
function initialTable()
{

  $conn = dbConnect();

  // sql to create table
  $sql = "CREATE TABLE IF NOT EXISTS userlist (
u varchar(50) NOT NULL,
p text NOT NULL,
d varchar(30) NOT NULL
PRIMARY KEY (`u`)
)";

  if ($conn->query($sql) === TRUE) {
     $result= true;
  } else {
     echo "Error creating table: " . $conn->error;
     $result= false; 
  }

  $conn->close();
  return $result;
}


//--------------------
// to do not store user/pass as text we use hash
//--------------------
function hashIt($item,$salty=false)
{
  if ($salty== false )
  {// no salt
    return crypt($item,'$2a$07$usesomesillystringforsalt$');
  }
  else
  {
    // A higher "cost" is more secure but consumes more processing power
    $cost = 10;

    // Create a random salt
    $size = mcrypt_get_iv_size(MCRYPT_CAST_256, MCRYPT_MODE_CFB);
    $iv = mcrypt_create_iv($size, MCRYPT_DEV_RANDOM);
    $salt = strtr(base64_encode($iv), '+', '.');

    // Prefix information about the hash so PHP knows how to verify it later.
    // "$2a$" Means we're using the Blowfish algorithm. The following two digits are the cost parameter.
    $salt = sprintf("$2a$%02d$", $cost) . $salt;

    // Hash the password with the salt
    return crypt($item, $salt);
  }
}


//---------------------
// compare hashes
//---------------------
function hashOk($hashed,$original,$salty=false)
{
  if ($salty == false)
  { //no salt was added
     return hash_equals($hashed, crypt($original));
  }
  else
  { // Hashing the item with its hash as the salt returns the same hash
     return hash_equals($hashed, crypt($original, $hashed));
  }
}

